// SingleRecipe.jsx
import { useLocation } from "react";
import { useParams, Link } from "react-router-dom";
import classes from "../common.module.css";

export default function SingleRecipe() {
  const { recipeId } = useParams();

  // Odczytuje dane przepisu z 'location.state'
  const { state } = useLocation();
  const selectedRecipe = state ? state.recipe : null;

  if (!selectedRecipe) {
    // Obsłuż sytuację, gdy przepis nie został przekazany
    return <p>Recipe not found.</p>;
  }

  const { img, title, publisher, description, publisherWebpage } =
    selectedRecipe;

  return (
    <div className="singleRecipeMainContainer">
      <img src={img} alt="food img" />

      <div className="singleRecipeDescriptionContainer">
        <h1>{title}</h1>
        <h6>Provided by {publisher}</h6>
        <p>{description}</p>

        <Link to="/RecipeList">
          <button className={classes.button}>Back</button>
        </Link>
        <a
          href={publisherWebpage}
          target="_blank"
          rel="noopener noreferrer"
          className="Btn"
        >
          Publisher Webpage
        </a>
        <p>{recipeId}</p>
      </div>
    </div>
  );
}
