export default function RecipePage() {
  const [search, setSearch] = useState("");
  const [recipes, setRecipes] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);

  async function getRecipesByIngredients() {
    try {
      const resp = await axios.get(
        "https://api.spoonacular.com/recipes/findByIngredients",
        {
          params: { apiKey: "TWÓJ_API_KEY", ingredients: search },
        }
      );

      // Wyświetl pełną odpowiedź w konsoli
      console.log("Full API response:", resp);

      setRecipes(resp.data);
    } catch (error) {
      console.error("Error fetching recipes:", error);
    }
  }

  const handleChange = (e) => {
    setSearch(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (search.trim() !== "") {
      console.log("jestem tutaj recipe page");
      await getRecipesByIngredients(search);
      setSearchClicked(true);
    } else {
      alert("Search field is empty. Please enter ingredients.");
    }
  };

  return (
    <>
      <MainNavigation />
      <Search
        search={search}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
      />
      <RecipeList
        recipes={recipes}
        searchTerm={search}
        searchClicked={searchClicked}
      />
    </>
  );
}
