import React from "react";
import { useParams, Link } from "react-router-dom";
import classes from "../common.module.css";

export default function SingleRecipe({ recipes }) {
  const { recipeId } = useParams();

  const recipeProps = {
    img: "adres_obrazka",
    title: "Tytuł przepisu",
    publisher: "Nazwa wydawcy",
    description: "Opis przepisu",
    publisherWebpage: "adres_strony_wydawcy",
  };

  const { image, title, publisher, description, publisherWebpage } =
    recipeProps;

  return (
    // <div className="singleRecipeMainContainer">

    //   <img src={recipes.image} alt="food img" />

    //   <div className="singleRecipeDescriptionContainer">
    //     <h1>{title}</h1>
    //     <h6>Provided by {publisher}</h6>
    //     <p>{description}</p>

    //     <Link to="/RecipeList">
    //       <button className={classes.button}>Back</button>
    //     </Link>
    //     <a
    //       href={publisherWebpage}
    //       target="_blank"
    //       rel="noopener noreferrer"
    //       className="Btn"
    //     >
    //       Publisher Webpage
    //     </a>
    //     <p>{recipeId}</p>
    //   </div>
    // </div>
    <div className={classes.RecipListMainContainer}>
      <h1>Recipe List</h1>
      {recipes.length > 0 ? (
        <ul>
          {recipes.map((recipe) => (
            <li key={recipe.id} className={classes.recipeItem}>
              <img src={recipe.image} alt="food img" />
              <h3>{recipe.title}</h3>
              <p>Provided by Spoonacular </p>
              <p>{recipe.summary}</p>
              <div className={classes.ButtonContainer}>
                <Link
                  to={`/singleRecipe/${recipe.id}`}
                  className={classes.detailButton}
                >
                  Detail
                </Link>
                <a
                  href={recipe.source_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={classes.recipeUrlButton}
                >
                  Recipe URL
                </a>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No recipes found.</p>
      )}
    </div>
  );
}
