import MainNavigation from "../components/MainNavigation";
import Recipe from "../components/Recipe";
import RecipeList from "../components/RecipeList";
import Search from "../components/Search";
import React, { useState, useEffect } from "react";
import axios from "axios";

export default function RecipePage() {
  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState("");

  async function getRecipesBySearch() {
    try {
      const apiKey = "164af9704a534bacb1c4fbb6086ed10b";

      let resp = await axios.get(
        `https://api.spoonacular.com/recipes/complexSearch?apiKey=${apiKey}&query=${search}`
      );

      setRecipes(resp.data.results);
    } catch (e) {
      console.log(e);
    }
  }

  useEffect(() => {
    if (search.trim() !== "") {
      getRecipesBySearch();
    }
  }, [search]);

  const handleChange = (e) => {
    setSearch(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Opcjonalnie, możesz wywołać getRecipesBySearch() tutaj, aby pobrać przepisy natychmiast po wysłaniu formularza.
  };

  return (
    <>
      <MainNavigation />
      <Recipe />
      <Search
        search={search}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
      />
      <RecipeList recipes={recipes} />
    </>
  );
}
