import MainNavigation from "../components/MainNavigation";
import Recipe from "../components/Recipe";
import RecipeList from "../components/RecipeList";
import Search from "../components/Search";
import React, { useState, useEffect } from "react";
import axios from "axios";

export default function RecipePage() {
  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState("");

  async function getRecipesBySearch() {
    try {
      // const apiKey = {REACT_APP_API_RECIP_BOOK_KEY};

      let resp = await axios.get(
        ` https://api.spoonacular.com/recipes/findByIngredients?apiKey=${process.env.REACT_APP_API_RECIP_BOOK_KEY}ingredients=apples,+flour,+sugar&number=2 `
        // https://api.spoonacular.com/recipes/findByIngredients?apiKey=${process.env.REACT_APP_API_RECIP_BOOK_KEY}&ingredients=${search}`
        // `https://api.spoonacular.com/recipes/complexSearch?apiKey=${process.env.REACT_APP_API_RECIP_BOOK_KEY}&query=${search}`
      );

      setRecipes(resp.data.results);
    } catch (e) {
      console.log(e);
    }
  }

  const handleChange = (e) => {
    setSearch(e.target.value);
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (search.trim() !== "") {
      await getRecipesBySearch();
    }
  };
  console.log(recipes);
  return (
    <>
      <MainNavigation />
      {/* <Recipe /> */}
      <Search
        search={search}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
      />
      <RecipeList recipes={recipes} />
    </>
  );
}
