import React from "react";
import { Link } from "react-router-dom";
import classes from "./RecipList.module.css";

const RecipeList = ({ recipes }) => {
  return (
    <div className={classes.RecipListMainContainer}>
      <h1>Recipe List</h1>
      {recipes.length > 0 ? (
        <ul>
          {recipes.map((recipe) => (
            <li key={recipe.id} className={classes.recipeItem}>
              <img src={recipe.image} alt="food img" />
              <h3>{recipe.title}</h3>
              <p>Provided by Spoonacular </p>
              <p>{recipe.summary}</p>
              <div className={classes.ButtonContainer}>
                {/* Przekazuje ca≈Çy obiekt przepisu jako parametr 'recipe' */}
                <Link
                  to={{
                    pathname: `/singleRecipe/${recipe.id}`,
                    state: { recipe },
                  }}
                  className={classes.detailButton}
                >
                  Detail
                </Link>
                <a
                  href={recipe.source_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={classes.recipeUrlButton}
                >
                  Recipe URL
                </a>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No recipes found.</p>
      )}
    </div>
  );
};

export default RecipeList;
