import React from "react";
import classes from "../common.module.css";

export default function SearchBar({ handleChange, handleSubmit, search }) {
  return (
    <div>
      <div>
        <h1>
          Search recipes with <strong>food@fork</strong>
        </h1>
        <form onSubmit={handleSubmit}>
          {/* Dodaj obsługę zdarzenia onSubmit do formularza */}
          <label htmlFor="search">Type recipes by comma</label>
          <div>
            <input
              type="text"
              name="search"
              placeholder="chicken, onion, carrots"
              value={search}
              onChange={handleChange}
            />
            <div>
              <button type="submit">Submit</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
