import axios from "axios";
import { useState, useEffect } from "react";
import MainNavigation from "../components/MainNavigation";

export default function SingleRecipePage({ match }) {
  const [recipeDetails, setRecipeDetails] = useState(null);

  useEffect(() => {
    const fetchRecipeDetails = async () => {
      const recipeId = match?.params?.id; // Dostosowano odczytywanie parametru

      if (!recipeId) {
        console.error("Recipe ID is missing.");
        return;
      }

      try {
        const response = await axios.get(
          `http://localhost:4000/getRecipeDetails/${recipeId}`
        );
        setRecipeDetails(response.data);
      } catch (error) {
        console.error("Error fetching recipe details:", error);
      }
    };

    fetchRecipeDetails();
  }, [match]); // Dostosowano zależność

  return (
    <>
      <MainNavigation />
      {recipeDetails ? (
        <div>
          <h2>{recipeDetails.title}</h2>
          {/* Wyświetl inne informacje o przepisie */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </>
  );
}
