import React from "react";
import { Link, useLocation } from "react-router-dom";
import classes from "../common.module.css";

const SingleRecipe = () => {
  const { state } = useLocation();

  // Sprawdź, czy obiekt state istnieje i czy zawiera właściwość recipe
  if (!state || !state.recipe) {
    // Możesz obsłużyć sytuację, gdy brakuje danych, np. przekierować na inną stronę
    return (
      <div>
        <p>Brak danych receptury</p>
        <Link to="../" className={classes.button}>
          Back
        </Link>
      </div>
    );
  }

  const { recipe } = state;

  return (
    <div className="singleRecipeMainContainer">
      <div className="singleRecipeDescriptionContainer">
        <h1>{recipe.title}</h1>
        <h6>Provided by {recipe.publisher}</h6>
        <p>{recipe.description}</p>

        <Link to="../" className={classes.button}>
          Back
        </Link>
        <a
          href={recipe.publisherWebpage}
          target="_blank"
          rel="noopener noreferrer"
          className="Btn"
        >
          Publisher Webpage
        </a>
        <p>{recipe.id}</p>
      </div>
    </div>
  );
};

export default SingleRecipe;
