import React from "react";
import { useParams, Link } from "react-router-dom";
import classes from "../common.module.css";

export default function SingleRecipe() {
  const { recipeId } = useParams();

  // Zakładam, że recipeProps zawiera wszystkie dane przepisu
  const recipeProps = {
    img: "adres_obrazka",
    title: "Tytuł przepisu",
    publisher: "Nazwa wydawcy",
    description: "Opis przepisu",
    publisherWebpage: "adres_strony_wydawcy",
  };

  const { img, title, publisher, description, publisherWebpage } = recipeProps;

  return (
    <div className="singleRecipeMainContainer">
      <img src={img} alt="food img" />

      <div className="singleRecipeDescriptionContainer">
        <h1>{title}</h1>
        <h6>Provided by {publisher}</h6>
        <p>{description}</p>

        <Link to="/RecipeList">
          <button className={classes.button}>Back</button>
        </Link>
        <a
          href={publisherWebpage}
          target="_blank"
          rel="noopener noreferrer"
          className="Btn"
        >
          Publisher Webpage
        </a>
        <p>{recipeId}</p>
      </div>
    </div>
  );
}
