import React from "react";
import { useParams, Link, useLocation } from "react-router-dom";
import classes from "./SinglePage.module.css";
import MainNavigation from "../components/MainNavigation";
const SingleRecipe = () => {
  const { id } = useParams();
  const { state } = useLocation();

  console.log("Location state:", state);

  if (!state || !state.recipe) {
    return (
      <div>
        <p>Brak danych receptury</p>
        <Link to="../" className={classes.button}>
          Back
        </Link>
      </div>
    );
  }

  const { recipe } = state;

  console.log("Recipe:", recipe);

  return (
    <div>
      <MainNavigation />
      <div className={classes.singleRecipeMainContainer}>
        <div className={classes.singleRecipeImgContainer}>
          <img src={recipe.image} alt="food img" />
        </div>
        <div className={classes.singleRecipeDescriptionContainer}>
          <h1>{recipe.title}</h1>
          <h6>Provided by {recipe.publisher}</h6>
          <p>{recipe.description}</p>

          <a
            href={recipe.publisherWebpage}
            target="_blank"
            rel="noopener noreferrer"
            className="Btn"
          >
            Publisher Webpage
          </a>
          <p>{recipe.id}</p>

          <Link to="../" className={classes.button}>
            Back
          </Link>
        </div>
      </div>
    </div>
  );
};

export default SingleRecipe;
